definition.script:
node {
    stage('Test') {
        def build = build propagate: false, job: 'Test';
        def result = build.result;

        if (result != 'SUCCESS') {
            def testResult = readFile file: 'TestResults.txt', encoding: 'UTF8';
            error '\n' + testResult;
        }
    }

    stage('Build Player') {
        def build = build propagate: false, job: 'BuildPlayer';
        def result = build.result;

        if(result != 'SUCCESS') {
            error 'fail to build player';
        }
    }
}
